definition.script:
pipeline{

    agent any

    environment{
        NUMBER = 10
    }

    stages{

        stage('stage-1'){
            steps{
                bat "docker run -e NUMBER=${NUMBER} kingabhisha/square-finder"
            }
        }

        stage('stage-2'){

            environment{
                NUMBER = 20
            }

            steps{
                bat "docker run -e NUMBER=${NUMBER} kingabhisha/square-finder"
            }
        }

        stage('stage-3'){
            steps{
                bat "docker run -e NUMBER=${NUMBER} kingabhisha/square-finder"
            }
        }
    }

    post{
        always{
            bat "docker system prune -f"
        }
    }

}
